/* General Form Styles */
.web-form-header {
    margin-bottom: 2rem;
    border: 2px solid #6c63ff; /* A modern purple color */
    border-radius: 15px;
    background: linear-gradient(45deg, #ff8a00, #e52e71); /* A gradient background */
    color: white;
    padding: 1.5rem;
    text-align: center;
}

.web-form-head {
    font-size: 2rem;
    font-weight: 700;
    letter-spacing: 1px;
}

.form-subheading {
    font-size: 1rem;
    font-weight: 300;
    margin-top: 0.5rem;
}

/* Form Styling */
.web-form {
    border: none;
    border-radius: 10px;
    padding: 2rem;
    background: #f9f9f9;
    max-width: 600px;
    margin: 0 auto;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1); /* Subtle shadow */
    transition: box-shadow 0.3s ease-in-out;
}

/* Form Hover Effect */
.web-form:hover {
    box-shadow: 0 12px 40px rgba(0, 0, 0, 0.15);
}

/* Form Group Styling */
.form-group {
    margin-bottom: 1.5rem;
}

label {
    display: block;
    font-weight: 600;
    color: #333;
    margin-bottom: 0.5rem;
    font-size: 1rem;
}

input, textarea {
    width: 100%;
    padding: 1rem;
    border-radius: 8px;
    font-size: 1rem;
    border: 1px solid #ccc;
    background: #fff;
    margin-top: 0.5rem;
    transition: all 0.3s ease;
}

input:focus, textarea:focus {
    border-color: #6c63ff; /* Focused input border color */
    outline: none;
    box-shadow: 0 0 8px rgba(108, 99, 255, 0.5); /* Soft glow on focus */
}

/* Styling for Textarea */
textarea {
    height: 150px;
    resize: vertical; /* Allows user to resize vertically */
}

/* Button Styling */
button {
    background-color: #6c63ff;
    color: white;
    padding: 1rem 2rem;
    border: none;
    border-radius: 25px;
    cursor: pointer;
    font-size: 1.1rem;
    width: 100%;
    transition: all 0.3s ease;
}

button:hover {
    background-color: #e52e71; /* Gradient hover effect */
    transform: translateY(-3px); /* Slight lift on hover */
}

button:active {
    transform: translateY(0); /* Button returns to normal on click */
}

/* Responsive Design */
@media (max-width: 600px) {
    .web-form {
        padding: 1.5rem;
    }

    .web-form-header {
        margin-bottom: 1rem;
    }

    .web-form-head {
        font-size: 1.75rem;
    }

    .form-subheading {
        font-size: 0.9rem;
    }
}
